import React from 'react';
import './css/login.css';
import B from './img/b.png';

class Login extends React.Component {
	constructor(props) {
	    super(props);
	    this.email = {value: ''};
	    this.pass = {value: ''};

	    this.handleEmailChange    = this.handleEmailChange.bind(this);
	    this.handlePasswordChange = this.handleEmailChange.bind(this);
	    this.handleSubmit         = this.handleSubmit.bind(this);
		}

	handleEmailChange(event) {
	   this.setState({value: event.target.value});
	}
	handlePasswordChange(event) {
	   this.setState({value: event.target.value});
	}

	handleSubmit(event) {
	    alert('A name was submitted: ' + this.state.value);
	    event.preventDefault();
  	}

  render() {
    return (
    	<>
		    <div className="fondo">
		    	<form className="form-signin login-f"  onSubmit={this.handleSubmit}>
				  <div className="text-center mb-4">
				    <img className="mb-4" src={B} alt="" width="72" height="72"/>
				    <h1 className="h3 mb-3 font-weight-normal">Shopping</h1>
				    <p>Si aun no te has registrado. Presiona aqui<a className="link" href="..."> Registrate</a>.</p>
				  </div>

				  <div className="form-label-group">
				   <label htmlFor="inputEmail">Correo electronico</label>
				    <input type="email" name="email" id="inputEmail" className="form-control" placeholder="Correo electronico" required autoFocus value={this.email.value} onChange={this.handleEmailChange} />
				   
				  </div>

				  <div className="form-label-group">
				  <label htmlFor="inputPassword">Contraseña</label>
				    <input type="password" name="password" id="inputPassword" className="form-control" placeholder="Contraseña" required  value={this.pass.value} onChange={this.handlePasswordChange} />
				    
				  </div>

				  <div className="checkbox mb-3">
				    <label>
				      <input type="checkbox" value="remember-me"/> Recuerdame
				    </label>
				  </div>
				  <input className="btn btn-lg btn-primary btn-block" type="submit" value="Submit" />
				  
				 </form>
		    </div>
    	</>
    );
  }
   
}

export default Login;
